import React from 'react';
import Board from './Board';
import Entry from './Entry';
import WordList from './WordList';

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      dictionary: ["cat"], //require('./data/words_dictionary.json'),
      wordList: [],
    }
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(word) {
    let valid = word in this.state.dictionary;
    if (valid) {
      this.setState({
        wordList: [...this.state.wordList, word],
      })
    }
    return valid;
  }

  flipTile(tile) {
    return;
  }

  render() {
    return (
      <div>
        <Board />
        <Entry handleSubmit={(word) => this.handleSubmit(word)} />
        <WordList wordList={this.state.wordList} />
      </div>
    );
  }
}

export default Game;
