import React from 'react';
import Board from './Board';
import Entry from './Entry';
import WordList from './WordList';

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      dictionary: require("./data/words_dictionary.json"),
      wordList: [],
      flippedTiles: [],
    }
  }

  handleSubmit(word) {
    let valid;
    if (!(word in this.state.dictionary)) {
      valid = false;
    }
    else {
      valid = true;
      // TODO: check if word can be made from tiles, steals, etc.
    }
    if (valid) {
      this.setState({
        wordList: [...this.state.wordList, word],
      })
    }
    return valid;
  }

  handleFlip(tile) {
    this.setState({
      flippedTiles: [...this.state.flippedTiles, tile]
    });
  }

  render() {
    return (
      <div>
        <Board onFlip={(tile) => handleFlip(tile)}/>
        <Entry onSubmit={(word) => this.handleSubmit(word)} />
        <WordList wordList={this.state.wordList} />
      </div>
    );
  }
}

export default Game;
