import './Entry.css';

class Entry extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      message: '',
      wordList: '',
    }
  }

  handleSubmit() {
    this.setState({
      wordList: [...this.state.wordList, this.state.message],
      message: '',
    })
  }

  handleChange(event) {
    this.setState({
      wordList: this.state.wordList,
      message: event.target.value,
    })
  }

  handleKeyPress(e) {
    if (e.key === "Enter") {
      handleSubmit();
    }
  }

  render() {
    const wordListElt = wordList.map((elt, idx) =>
      <li key={idx}>{elt}</li>
    )

    return (
      <div className="Entry">
        <input
          className="answer"
          onKeyPress={handleKeyPress}
          onChange={handleChange}
          value={message}
        />

        <h2>Words:</h2>
        <ul>{wordListElt}</ul>
      </div>
    );
  }
}

export default Entry;
