import React, { useState, useEffect } from "react";
import "./Entry.css";

function Entry(props) {
  let [message, setMessage] = useState("");
  let [wordList, setWordList] = useState([]);

  useEffect(() => {
    const handleWordResponse = (b) => {
      if (b) {
        setMessage("");
      }
    };
    const handleUpdateWordList = (lst) => {
      setWordList(lst);
    };

    socket.on("wordResponse", handleWordResponse);
    socket.on("updateWordList", handleUpdateWordList);

    return () => {
      socket.off("wordResponse", handleWordResponse);
      socket.off("updateWordList", handleUpdateWordList);
    };
  }, [props.socket]);

  const handleSubmit = () => {
    let word = message;
    props.socket.emit("word", word);
  };

  const handleChange = (event) => {
    setMessage(event.target.value);
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      handleSubmit();
    }
  };

  return (
    <div className="Entry">
      <input
        className="answer"
        onKeyPress={(event) => handleKeyPress(event)}
        onChange={(event) => handleChange(event)}
        value={message}
        ref={props.passedRef}
      />
    </div>
  );
}

export default Entry;
